function TextForm(props) {
    return (
        <>
        <h1>{props.heading}</h1>
        <div className="myBox">
  <label for="myBox" className="form-label">Enter text</label>
  <textarea className="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
</div>
</>
    )
   
}

export default TextForm
